/*
Script Include : QuoteWorkflowUtil
Method         : resetQuoteApprovalsOnSubmit
params         : 1 - Quote, 2-rejectCode Status, 3-Reason for Rejection, 4 - Reset all the approvals for the current Quote 

Description :
Check whether state (!= not_required) does exist in sysapproval_approver table. If exists, then retrieve the records. Then, update the record with the value (state = not_required) and comments.

QuoteWorkflowUtil.resetQuoteApprovalsOnSubmit = function(quote, rejectionCode, rejectionReason, resetAll) {
	quote.sales_quote_comments = rejectionReason;
	quote.u_edit_approval_workflow_metric = true;
	
	// Reset the approvals
	var approval = new GlideRecord('sysapproval_approver');
	approval.addQuery('document_id', quote.sys_id);
	if (!resetAll) {
		approval.addQuery('state', 'IN', 'requested,not requested,cancelled');
	} else {
		approval.addQuery('state', '!=', 'not_required');
	}
	approval.query();
	
	while (approval.next()) {
		approval.state = 'not_required';
		approval.comments = rejectionReason;
		approval.update();
	}
	
}

*/

